    // Las siguientes dos lineas se ponen siempre para el manejo de sesiones
	ob_start();
	if(!isset($_SESSION)) 
    { 
        session_start(); 
    } 
	
	include('../utils/htdb.php');
	include('../utils/authorization.php');
	
	// Funcion para comprobar si es un asistente o administrador
	checkAuth('authLevel','1','redirect');
	
	// Query a la BD para enlistar TODOS los laboratorios que contienen el string de busqueda 
	// en el campo de salon o recursos disponibles
	$query = "SELECT `Lab`.* FROM `Lab` WHERE";
	$query .= " (`room` LIKE '%".$_POST['search']."%' OR `available_resources` LIKE '%".$_POST['search']."%'";
	if(stristr($_POST['search'],"si")){
		$query .= " OR `special_lab` = 1";
	}elseif(stristr($_POST['search'],"no")){
		$query .= " OR `special_lab` = 0";
	}
	$query.= ")";
	$db->consulta($query);
	$labs = $db->tomaResultado();
	
	if(count($labs) == 0){
		?>
		<tr>
			<td colspan="20" align="center"><i>No hay laboratorios bajo esa b&uacute;squeda</i></td>
		</tr>
		<?
	}
	
	// Se listan los resultados de la query en una tabla
	foreach($labs as $lab) { 
?>
		<tr>
			<td class="center"><?=$lab['room']?></td>
			<td><?=$lab['available_resources']?></td>
			<td><? if($lab['special_lab'] == 0) {echo 'No';} else {echo 'Si';}?></td>
			<td><a href="lab/edit/<?=$lab['id']?>">Editar</a></td>
<? 			if(checkAuth('authLevel','2','boolean')){ ?>
